import React from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';


class Register extends React.Component{
  state = {
      users: [] ,
      username: '',
      email:'',
      password:'',
      msg: null
    };

    static propTypes = {
        isAuthenticated:PropTypes.bool,
        error: PropTypes.object.isRequired
    }
  

  

  getUsers= async () =>{
    const res = await axios.get ('http://localhost:8080/api/users');
    this.setState({users: res.data});
  }

  onChange = e =>{
    this.setState({ [e.target.name]: e.target.value })
  }

  onSubmit = async e =>{
    e.preventDefault();

    const user = {
      name: this.state.username,
      email:this.state.email,
      password:this.state.password
    }
    
    const res = await axios.post('http://localhost:8080/api/auth/register', user)
    

    this.getUsers()

    console.log(this.state.users)

    this.setState({
      username: '',
      email:'',
      password:''
    })


  }


    render(){
        return( 
        <form onSubmit= { this.onSubmit }>
            <h1>Register---</h1>
          <h1>Unite a nuestra comunidad!</h1>


          <div className="form-group">
            <label className="control-label" for='name'>Username</label>
            <input
              onChange= { this.onChange }
              type="text"
              name="name"
              id="name"
              placeholder="Name"
              className="form-control"
              />
          </div>

          <div className="form-group">
            <label className="control-label" for='email'>Email</label>
            <input
              onChange= { this.onChange }
              type="email"
              name="email"
              id="email"
              placeholder="email"
              className="form-control"
              />
          </div>

          <div className="form-group">
            <label className="control-label" for='password'>Password</label>
            <input
              onChange= { this.onChange }
              type="password"
              name="password"
              id="password"
              placeholder='password'
              className="form-control"
              />
          </div>


          <div className="form-group">
            <button className="btn btn-primary btn-lg">
              Sign up
            </button>
          </div>


        </form>
        
        );
  }
}

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated,
    error: state.error
});



export default connect(mapStateToProps,{  })(Register);
