import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'reactstrap';
import { connect } from 'react-redux';
import { getItems } from '../actions/itemActions';
import PropTypes from 'prop-types';

class ItemList extends Component{
    constructor() {
        super()
        this.state = {
          filteredCities: [],
          cityFilter: ""
        }
      }

    async componentDidMount() {
       await this.props.getItems();
       await this.setState({
        filteredCities: this.props.item.items
      })
       
    }


    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
        this.filterCities(this.state.cityFilter)
      }

      filterCities (cityFilter) {
        let filteredCities = this.props.item.items
        filteredCities = filteredCities.filter((city) => {
          let cityName = city.name.toLowerCase()
          return cityName.indexOf(
            cityFilter.toLowerCase()) !== -1
        })
        this.setState({
          filteredCities
        })
      }


    render(){
        //const { items }= this.props.item;
        
    
        return(
            <div>
            <div>
                <label htmlFor="filter">Filter by Cities: </label>
                <input type="text" id="filter" 
                value={this.state.cityFilter} 
                onChange={this.handleChange.bind(this)}/>
            </div>


            {this.state.filteredCities?
            
        <ListGroup>
            {this.state.filteredCities.map(({name}) => (
            <ListGroupItem> {name} </ListGroupItem>
            ))}
        </ListGroup>:null
        }
        </div>
    
        );
    }
}

ItemList.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}


const mapStateToProps = (state) => ({
    item: state.item

})

export default connect(mapStateToProps, { getItems })(ItemList);
