import React, { Component } from 'react';
import { ListGroup, ListGroupItem } from 'reactstrap';
import { connect } from 'react-redux';
import { getItems } from '../actions/itemActions';
import PropTypes from 'prop-types';

class ItemList extends Component{
    constructor() {
        super()
        this.state = {
          cities: [],
          filteredCities: [],
          cityFilter: ""
        }
      }

    componentDidMount() {
       this.props.getItems()
    }


    handleChange = (e) => {
        this.setState({
            cityFilter: e.target.value
        })
        filteredCities.onChange(event.target.value)
      }




    render(){
        const { items }= this.props.item;

        filterCities = (cityFilter) => {
            let filterCities = this.state.cities
            filterCities = filterCities.filter((city) => {
              let cityName = city.name.toLowerCase()
              return cityName.indexOf(
                cityFilter.toLowerCase()) !== -1
            })
            this.setState({
              filterCities
            })
          }



        
        return(
            <div>
            <div>
                <label htmlFor="filter">Filter by Poet: </label>
                <input type="text" id="filter" 
                value={this.state.cityFilter} 
                onChange={this.handleChange}/>
            </div>


            
        <ListGroup>
            {filterCities.map(({name}) => (
            <ListGroupItem> {name} </ListGroupItem>
            ))}
        </ListGroup>
        </div>
    
        );
    }
}

ItemList.propTypes = {
    getItems: PropTypes.func.isRequired,
    item: PropTypes.object.isRequired
}


const mapStateToProps = (state) => ({
    item: state.item
})

export default connect(mapStateToProps, { getItems })(ItemList);
