import React, { Component } from 'react';
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  NavLink,
  Alert
} from 'reactstrap';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { register } from '../actions/authActions';
import { clearErrors } from '../actions/errorsActions';

class Register extends Component {
  state = {
    modal: false,
    name:'',
    email: '',
    password: '',
    firstName:'',
    lastName:'',
    country:'',
    msg: null,
    checkbox: false
  };

  static propTypes = {
    isAuthenticated: PropTypes.bool,
    error: PropTypes.object.isRequired,
    register: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired
  };

  componentDidUpdate(prevProps) {
    const { error, isAuthenticated } = this.props;
    if (error !== prevProps.error) {
      // Check for register error
      if (error.id === 'REGISTER_FAIL') {
        this.setState({ msg: error.msg.msg });
      } else {
        this.setState({ msg: null });
      }
    }

    // If authenticated, close modal
    if (this.state.modal) {
      if (isAuthenticated) {
        this.toggle();
      }
    }
    function showRegister (c){
      console.log(c)
      if (c!==false) {
        return <Button color='dark' style={{ marginTop: '2rem' }} block>
                    Register
                  </Button>
      }else{
        this.setState({
          checkbox:false
        })
      }
    }
  }



  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal
    });
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const { name, email, password, firstName, lastName, country, checkbox } = this.state;

    //create user object
    const newUser = {
      name,
      email,
      password,
      firstName,
      lastName,
      country,
      checkbox
    };

    console.log(newUser.checkbox)

    // Attempt to register
    this.props.register(newUser);
  };

  render() {
    return (
      <div>
        <NavLink onClick={this.toggle} href='#'>
          Register
        </NavLink>

        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Register</ModalHeader>
          <ModalBody>
            {this.state.msg ? (
              <Alert color='danger'>{this.state.msg}</Alert>
            ) : null}
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
              <Label for='userName'>UserName</Label>
                <Input
                  type='text'
                  name='name'
                  id='name'
                  placeholder='Name'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='email'>Email</Label>
                <Input
                  type='email'
                  name='email'
                  id='email'
                  placeholder='Email'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='password'>Password</Label>
                <Input
                  type='password'
                  name='password'
                  id='password'
                  placeholder='Password'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='firstName'>First Name</Label>
                <Input
                  type='text'
                  name='firstName'
                  id='firstName'
                  placeholder='First Name'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='lastName'>Last Name</Label>
                <Input
                  type='text'
                  name='lastName'
                  id='lastName'
                  placeholder='Last Name'
                  className='mb-3'
                  onChange={this.onChange}
                />

<div className="input-group mb-3">
  <div className="input-group-prepend">
    <label className="input-group-text" >Country</label>
  </div>

  <select className="custom-select" 
            id="country" 
            name='country'
            onChange={this.onChange}>

      <option defaultValue>Choose Country</option>

      <option>England</option>
      <option>France</option>
      <option>Germany</option>
      <option>Holand</option>
      <option>Ireland</option>
      <option>Spain</option>
      <option>United State</option>
  </select>
</div>

<div>

      <input type="checkbox" 
      id="checkbox" 
      name="checkbox" 
      onChange={this.onChange}
      /> 
      <span>I agree to MYtinerary's <a href="#">Terms & Conditions</a></span>

</div>
              {this.showRegister(this.state.checkbox)}

              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  error: state.error
});

export default connect(
  mapStateToProps,
  { register, clearErrors }
)(Register);
