import React, { Component } from 'react';
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  NavLink,
  Alert
} from 'reactstrap';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { login } from '../actions/authActions';
import { clearErrors } from '../actions/errorsActions';

class LoginRedux extends Component {
  state = {
    modal: false,
    username:'',
    email: '',
    password: '',
    firstName:'',
    lastName:'',
    country:'',
    msg: null
  };

  static propTypes = {
    isAuthenticated: PropTypes.bool,
    error: PropTypes.object.isRequired,
    login: PropTypes.func.isRequired,
    clearErrors: PropTypes.func.isRequired
  };

  componentDidUpdate(prevProps) {
    const { error, isAuthenticated } = this.props;
    if (error !== prevProps.error) {
      // Check for register error
      if (error.id === 'LOGIN_FAIL') {
        this.setState({ msg: error.msg.msg });
      } else {
        this.setState({ msg: null });
      }
    }

    // If authenticated, close modal
    if (this.state.modal) {
      if (isAuthenticated) {
        this.toggle();
      }
    }
  }

  toggle = () => {
    // Clear errors
    this.props.clearErrors();
    this.setState({
      modal: !this.state.modal
    });
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    const { username, email, password, firstName, lastName, country} = this.state;

    const user = {
      username,
      email,
      password,
      firstName,
      lastName,
      country
    };

    // Attempt to login
    this.props.login(user);
  };

  render() {
    return (
      <div>
        <NavLink onClick={this.toggle} href='#'>
          Login
        </NavLink>

        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Login</ModalHeader>
          <ModalBody>
            {this.state.msg ? (
              <Alert color='danger'>{this.state.msg}</Alert>
            ) : null}
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
              <Label for='userName'>Username</Label>
                <Input
                  type='text'
                  name='username'
                  id='username'
                  placeholder='Username'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='email'>Email</Label>
                <Input
                  type='email'
                  name='email'
                  id='email'
                  placeholder='Email'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='password'>Password</Label>
                <Input
                  type='password'
                  name='password'
                  id='password'
                  placeholder='Password'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='firstName'>First Name</Label>
                <Input
                  type='text'
                  name='firstName'
                  id='firstName'
                  placeholder='First Name'
                  className='mb-3'
                  onChange={this.onChange}
                />

                <Label for='lastName'>Last Name</Label>
                <Input
                  type='text'
                  name='lastName'
                  id='lastName'
                  placeholder='Last Name'
                  className='mb-3'
                  onChange={this.onChange}
                />

<div className="input-group mb-3">
  <div className="input-group-prepend">
    <label className="input-group-text" >Country</label>
  </div>
  <select className="custom-select" id="country">


    <option name='country'
            id='country'
            onChange={this.onChange}>England</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>France</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>Germany</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>Holand</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>Ireland</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>Spain</option>
    <option name='country'
            id='country'
            onChange={this.onChange}>United State</option>
  </select>
</div>



                <Button color='dark' style={{ marginTop: '2rem' }} block>
                  Login
                </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  error: state.error
});

export default connect(
  mapStateToProps,
  { login, clearErrors }
)(LoginRedux);
